import React from 'react';
import { Position, NodeProps } from '@xyflow/react';
import styles from './ArgsNode.module.scss';
import { StandardHandle } from '../../Handles';
import { Workflows } from '@tapis/tapis-typescript';
import { Edit, Delete, ErrorOutline } from '@mui/icons-material';
import { useHistory } from 'react-router-dom';
import { Tooltip } from '@mui/material';

type NodeType = {
  pipeline: Workflows.Pipeline;
  showIO: boolean;
  // Params that are referenced in other tasks either correctly or erroneously
  referencedKeys: Array<string>;
};

const argImageSrc =
  'data:image/png;base64,';

const ArgsNode: React.FC<NodeProps> = ({ data }) => {
  const { pipeline, referencedKeys, showIO } = data as NodeType;
  const params = pipeline.params || {};
  // References from tasks to env variables that do not exist
  const missingRefs = referencedKeys.filter(
    (k) => !Object.keys(params).includes(k)
  );

  return (
    <>
      <div className={styles['node']}>
        <div className={styles['body']}>
          <div className={styles['header']}>
            <img src={argImageSrc} className={styles['header-img']} />
            <span className={styles['title']}>Arguments</span>
          </div>
        </div>
        <div>
          {Object.keys(params).length > 0 && (
            <div className={styles['io']}>
              {Object.keys(params).map((key) => {
                return (
                  <div
                    className={styles['io-item']}
                    style={{ position: 'relative' }}
                  >
                    <div>
                      <StandardHandle
                        id={`arg-${key}`}
                        type="source"
                        position={Position.Right}
                      />
                    </div>
                    <div style={{ textAlign: 'right' }}>
                      <Tooltip title={key}>
                        <span>{key}</span>
                      </Tooltip>
                    </div>
                  </div>
                );
              })}
            </div>
          )}
          {missingRefs.length > 0 && showIO && (
            <div className={styles['io']}>
              {missingRefs.map((key) => {
                return (
                  <div
                    className={`${styles['io-item']} ${styles['io-item-error']}`}
                    style={{ position: 'relative' }}
                  >
                    <div>
                      <StandardHandle
                        id={`arg-${key}`}
                        type="source"
                        position={Position.Right}
                      />
                    </div>
                    <div style={{ textAlign: 'right' }}>
                      <Tooltip
                        title={`Parameter '${key}' is referenced by some task(s) but does not exist. Either add this parameter to the pipeline or remove the task input(s) that references it.`}
                      >
                        <div>
                          <span>{key}</span>
                          <ErrorOutline
                            fontSize="small"
                            sx={{ marginLeft: '8px', color: 'red' }}
                          />
                        </div>
                      </Tooltip>
                    </div>
                  </div>
                );
              })}
            </div>
          )}
        </div>
      </div>
    </>
  );
};

export default ArgsNode;
