import React, { useState } from 'react';
import { Position, NodeProps } from '@xyflow/react';
import styles from './TaskNode.module.scss';
import { StandardHandle } from '../../Handles';
import { Workflows } from '@tapis/tapis-typescript';
import { Edit, Delete } from '@mui/icons-material';
import { useHistory } from 'react-router-dom';
import { TaskUpdateProvider } from 'app/Workflows/_context';
import { DeleteTaskModal } from 'app/Workflows/_components/Modals';

type NodeType = {
  task: Workflows.Task;
  tasks: Array<Workflows.Task>;
  groupId: string;
  pipelineId: string;
};

const resolveImageBuildNodeImage = (builder: Workflows.EnumBuilder) => {
  switch (builder) {
    case Workflows.EnumBuilder.Kaniko:
      return 'data:image/png;base64,';
    case Workflows.EnumBuilder.Singularity:
      return 'data:image/png;base64,';
  }
};

const resolveFunctionTaskRuntimeImage = (
  runtime: Workflows.EnumRuntimeEnvironment
) => {
  if (runtime.includes('pytorch')) {
    return 'data:image/jpeg;base64,';
  }

  if (runtime.includes('tensorflow')) {
    return 'data:image/png;base64,';
  }

  if (runtime.includes('python')) {
    return 'data:image/png;base64,';
  }

  if (runtime.includes('node')) {
    return 'data:image/png;base64,';
  }

  return 'data:image/png;base64,';
};

const resolveNodeImage = (task: Workflows.Task) => {
  switch (task.type) {
    case Workflows.EnumTaskType.Function:
      return resolveFunctionTaskRuntimeImage(task.runtime!);
    case Workflows.EnumTaskType.TapisJob:
      return 'data:image/png;base64,';
    case Workflows.EnumTaskType.Template:
      return 'data:image/png;base64,';
    case Workflows.EnumTaskType.ImageBuild:
      return resolveImageBuildNodeImage(task.builder!);
    default:
      return 'https://t3.ftcdn.net/jpg/06/54/14/70/360_F_654147004_3mnMUDadkASxqC2xJyhkuFuG0QMPIqMR.jpg';
  }
};

const TaskNode: React.FC<NodeProps> = ({ data }) => {
  const [modal, setModal] = useState<string | undefined>(undefined);
  const history = useHistory();
  let { task, tasks, groupId, pipelineId } = data as NodeType;

  return (
    <>
      <TaskUpdateProvider
        task={task}
        tasks={tasks}
        groupId={groupId}
        pipelineId={pipelineId}
      >
        <StandardHandle type="target" position={Position.Left} />
        <div className={styles['node']}>
          <div className={styles['header']}>
            <img
              src={resolveNodeImage(task)}
              className={styles['header-img']}
            />
            <span className={styles['title']}>{task.id}</span>
          </div>
          <div className={styles['body']}>
            <i className={styles['description']}>
              {task.description || 'No description'}
            </i>
          </div>
          <div className={styles['footer']}>
            <Edit
              className={styles['action']}
              onClick={() => {
                history.push(
                  `/workflows/pipelines/${groupId}/${pipelineId}/tasks/${task.id}`
                );
              }}
            />
            <Delete
              className={styles['action-danger']}
              color="error"
              onClick={() => {
                setModal('delete');
              }}
            />
          </div>
        </div>
        <DeleteTaskModal
          open={modal === 'delete'}
          toggle={() => setModal(undefined)}
        />
        <StandardHandle type="source" position={Position.Right} />
      </TaskUpdateProvider>
    </>
  );
};

export default TaskNode;
